mapper.select:
SELECT COUNT(*)
FROM USERS
WHERE EMAIL = #{email}

SELECT COUNT(*)
FROM USERS
WHERE NAME = #{name}

mapper.insert:
        INSERT INTO USERS(
                            EMAIL
                          , PASSWORD
                          , NAME
--                           , ADDRESS
                          , DETAIL_ADDRESS
        ) VALUES (
                   #{email}
                 , #{password}
                 , #{name}
--                  , #{address}
                 , #{detail_address}
                 )
